import React from "react";
import classes from "./Dropdown.module.scss";

const { useState, useEffect } = React;

// const data = [{id: 0, label: "Datos personales"}, {id: 1, label: "Paris, FR (CDG)"}];

const Dropdown = () => {
  const [isOpen, setOpen] = useState(false);
  const [items, setItem] = useState();
  const [selectedItem, setSelectedItem] = useState(null);

  const toggleDropdown = () => setOpen(!isOpen);

  const handleItemClick = (id) => {
    selectedItem == id ? setSelectedItem(null) : setSelectedItem(id);
  }

  return (
    <div className={classes.dropdown}>
      <div className='dropdown-header' onClick={toggleDropdown}>
        {selectedItem ? items.find(item => item.id == selectedItem).label : "Beneficiario 1"}
        <i className={`fa fa-chevron-right icon ${isOpen && "open"}`}></i>
      </div>
      <div className={`classes.dropdown-body ${isOpen && 'open'}`}>
        {items.map(item => (
          <div className={classes.dropdown-item} onClick={e => handleItemClick(e.target.id)} id={item.id}>
            <span className={`dropdown-item-dot ${item.id == selectedItem && 'selected'}`}>â€¢ </span>
            {item.label}
          </div>
        ))}
      </div>
    </div>
  )
}

export default Dropdown;
