import React from "react";
import classes from "./Stepper.module.scss";

function getStepContent(stepIndex) {
  switch (stepIndex) {
    case 0:
      return <div>Content of Step 1</div>;
    case 1:
      return <div>Content of Step 2</div>;
      case 2:
      return <div>Content of Step 3</div>;
    default:
      return "No Content";
  }
}

export default function Stepper() {
  const [activeStep, setActiveStep] = React.useState(0);

  const steps = ["Validacion de Identidad", "Datos necesarios","Firma de contratos" ];

  const handleNext = () => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  const handleReset = () => {
    setActiveStep(0);
  };

  return (
    <div>
      <div className={classes.steps}>
        <ul>
          <li className={classes.activeStep === 0 ? "active" : "normal"}>
            <span>1</span>
            <span>{steps[0]}</span>
          </li>
          <li className={classes.activeStep === 1 ? "active" : "normal"}>
            <span>2</span>
            <span>{steps[1]}</span>
          </li>
          <li className={classes.activeStep === 1 ? "active" : "normal"}>
            <span>3</span>
            <span>{steps[2]}</span>
          </li>
        </ul>
      </div>
      <div>
        {activeStep === steps.length ? (
          <div className={classes.stepscompleted}>
            <p>All steps completed</p>
            <span onClick={handleReset}>Reset</span>
          </div>
        ) : (
          <div className="stepped">
            <p>{getStepContent(activeStep)}</p>
            <div>
              {activeStep === 0 ? "" : <span onClick={handleBack}>Back</span>}
              <span onClick={handleNext}>
                {activeStep === steps.length - 1 ? "Submit" : "Next"}
              </span>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
